function addDotBeforeEachList(e,o){var i=e.children(o);i.css("position","relative"),i.each(function(e,o){$(o).append("<b class='dots-before-li'></b>").hover(function(){$(this).find(".dots-before-li").addClass("dots-before-li-active")},function(){$(this).find(".dots-before-li").removeClass("dots-before-li-active")})})}$(function(){addDotBeforeEachList($(".news"),"p")});